mapper.select:
select 1 as level,suc.user_id as userId,u.nickname as userName,sc.user_level as userLevel,up.nickname as parentUserName from wwq_share_user_concart suc left join wwq_user u on u.id=suc.user_id left join wwq_share_count sc on sc.user_id=u.id left join wwq_user up on up.id=suc.parent_id where parent_id=#{userId}
union
select 2 as level,suc.user_id as userId,u.nickname as userName,sc.user_level as userLevel,up.nickname as parentUserName from wwq_share_user_concart suc left join wwq_user u on u.id=suc.user_id left join wwq_share_count sc on sc.user_id=u.id left join wwq_share_user_concart sucp on suc.parent_id=sucp.user_id left join wwq_user up on up.id=suc.parent_id where sucp.parent_id=#{userId}
